##This module outputs figures. These include:# the geometry of the spin system##import osfrom matplotlib import pyplot as pltAtomsColor = {'1H': 'grey', '2H': 'k', '15N': 'b', '13C': 'orange', '19F': 'g', '31P': 'yellow'}AtomsSize = {'1H': 1.0, '2H': 1.0, '15N': 2.6, '13C': 2.8, '19F': 2.0, '31P': 4.0}def SpinSystemGeometry(SpinSystem, Coordinates, save = False, outputFolder = None, overwrite = False):            fig = plt.figure()    ax = fig.add_subplot(111, projection='3d')        for Labels, coord in Coordinates.items():        if coord != []:            ax.scatter([coord[0]], [coord[1]], [coord[2]], s = AtomsSize[SpinSystem.Nuclei[Labels]] * plt.rcParams['lines.markersize'] ** 2, color = AtomsColor[SpinSystem.Nuclei[Labels]])            ax.text(coord[0], coord[1], coord[2], Labels, color = AtomsColor[SpinSystem.Nuclei[Labels]])            ax.set_xlabel('x')    ax.set_ylabel('y')    ax.set_zlabel('z')    ax.set_title('Geometry of the spin system')        if save:        if overwrite:            plt.savefig(outputFolder + 'Geometry.pdf', format = 'pdf')                    else:            if not os.path.isfile(SpinSystem.DirOutName + 'Figure/Spinsystem/Geometry.pdf'):                plt.savefig(outputFolder + 'Geometry.pdf', format = 'pdf')                            else:                n = 1                filename = 'Geometry_' + str(n) + '.pdf'                while os.path.isfile(SpinSystem.DirOutName + '/Figure/Spinsystem/' + filename):                    n += 1                    filename = 'Geometry_' + str(n) + '.pdf'                                    plt.savefig(outputFolder + filename, format = 'pdf')        plt.show()    plt.close()                        