###This module produces the text outputs. This includes:#information about the spin system###import configparserdef write_spinsystem(SpinSystem, OutputFolder):        cfg = configparser.ConfigParser()        try:        cfg['NAME'] = {'Spin system': SpinSystem.Name}                for Label in SpinSystem.Nuclei.keys():            cfg[Label] = {}                        if SpinSystem.Coordinates[Label] != []:                cfg[Label]['x'] = str(SpinSystem.Coordinates[Label][0])                cfg[Label]['y'] = str(SpinSystem.Coordinates[Label][1])                cfg[Label]['z'] = str(SpinSystem.Coordinates[Label][2])                            if 'isotropic' in SpinSystem.CSA[Label].keys():                cfg[Label]['CSA'] = 'isotropic'            else:                for Component, Value in SpinSystem.CSA[Label].items():                    cfg[Label][Component] = str(Value)                            cfg['SCALAR COUPLINGS'] = {}        for nuclei1 in range(len(SpinSystem.Nuclei) - 1):            for nuclei2 in range(nuclei1 + 1, len(SpinSystem.Nuclei)):                if float(SpinSystem.CouplingConstants[nuclei1, nuclei2]) != 0.0:                    atoms = list(SpinSystem.Nuclei.keys())                    cfg['SCALAR COUPLINGS'][atoms[nuclei1] + ' - ' + atoms[nuclei2]] = str(SpinSystem.CouplingConstants[nuclei1, nuclei2])                                    cfg['BASIS'] = {}        if SpinSystem.BasisType != None:            cfg['BASIS']['Type'] = SpinSystem.BasisType            cfg['BASIS']['Operators'] = ''            for Op in SpinSystem.Basis.keys():                cfg['BASIS']['Operators'] += Op                cfg['BASIS']['Operators'] += ', '            cfg['BASIS']['Operators'] = cfg['BASIS']['Operators'][:-2]                            with open(OutputFolder + 'SpinSystem.toml', 'w') as f:            cfg.write(f)                    except:        print('An unknown error occured while writing information about the spin system. Please report to us.')        print()        